import React, {useEffect, useRef, useState} from 'react';
import {Chart} from 'primereact/chart';
import {ListBox} from 'primereact/listbox';
import {InputText} from 'primereact/inputtext';
import {Knob} from 'primereact/knob';
import {ShowcaseService} from '@/service/ShowcaseService';

const SignalChart = () => {
    const chartRef = useRef();
    const chartOptions = {
        maintainAspectRatio: false,
        animation: false,
        indexAxis: 'y',
        scales: {
            x: {
                min: 0,
                max: 100,
                type: 'linear',
                position: 'bottom',
                grid: {
                    color: '#3f4b5b'
                }
            },
            y: {
                grid: {
                    color: '#3f4b5b'
                }
            }
        },
        plugins: {
            legend: {
                display: false,
            }
        },
        onClick: (event, item) => {
            if (item.length > 0) {
                setSelectedSignal({
                    satelliteName: satelliteList[item[0].index],
                    towerId: item[0].datasetIndex + 1,
                    signalQuality: signalDatasets[item[0].datasetIndex].data[item[0].index],
                    label: `${satelliteList[item[0].index]}#${item[0].datasetIndex + 1}`,
                });
            }
        }
    };

    const [selectedSignal, setSelectedSignal] = useState({
        satelliteName: null,
        towerId: null,
        label: null
    });
    const [satelliteInfo, setSatelliteInfo] = useState({});
    const [listBoxOptions, setListBoxOptions] = useState([]);
    const [signalDatasets, setSignalDatasets] = useState([]);
    const [satelliteList, setSatelliteList] = useState([]);
    const [highlightDataset, setHighlightDataset] = useState({});
    const [selectedSignalDetails, setSelectedSignalDetails] = useState([]);

    useEffect(() => {
        initiateSatelliteInfo();
        initiateSatelliteSignalData();
    }, []);

    useEffect(() => {
        let satSelection = [];
        satSelection[0] = 150;

        const highlightImage = new Image(40, 40);
        highlightImage.src = `/images/chart/selection.png`;

        if (selectedSignal.satelliteName) {
            const satIndex = satelliteList.findIndex((satName) => satName === selectedSignal.satelliteName);
            satSelection[satIndex] = selectedSignal.signalQuality;
        }

        setHighlightDataset({
            pointStyle: highlightImage,
            showLine: false,
            radius: 10,
            hoverRadius: 20,
            label: 'Selected Signal',
            data: satSelection,
            backgroundColor: '#FFD54F'
        });
    }, [selectedSignal]);

    useEffect(() => {
        if (selectedSignal.satelliteName) {
            setSelectedSignalDetails({
                satelliteName: selectedSignal.satelliteName,
                towerId: selectedSignal.towerId,
                signalQuality: selectedSignal.signalQuality,
                dimensions: satelliteInfo.satelliteAttributes[selectedSignal.satelliteName].dimensions,
                perigeeAltitude: satelliteInfo.satelliteAttributes[selectedSignal.satelliteName].perigeeAltitude,
                apogeeAltitude: satelliteInfo.satelliteAttributes[selectedSignal.satelliteName].apogeeAltitude,
                city: satelliteInfo.towerAttributes[selectedSignal.towerId - 1].city,
                latitude: Math.abs(satelliteInfo.towerAttributes[selectedSignal.towerId - 1].latitude),
                latitudePole:
                    satelliteInfo.towerAttributes[selectedSignal.towerId - 1].latitude > 0 ? '° N' : '° S',
                longitude: Math.abs(satelliteInfo.towerAttributes[selectedSignal.towerId - 1].longitude),
                longitudeHemisphere:
                    satelliteInfo.towerAttributes[selectedSignal.towerId - 1].longitude > 0 ? '° E' : '° W',
            });
        }
    }, [selectedSignal]);

    const initiateSatelliteInfo = () => {
        ShowcaseService.fetchSatelliteInformation()
            .then(data => {
                setSatelliteInfo(data);
            })
            .catch(error => {
                console.error(error);
            });
    };

    const initiateSatelliteSignalData = () => {
        ShowcaseService.fetchSatelliteSignalData()
            .then(data => {
                let towerCount = 0;
                let listBoxOptions = [];
                let satList = [];
                let datasets = [];

                data.forEach(satellite => {
                    satellite.signals.forEach(signal => {
                        towerCount = Math.max(towerCount, signal.towerId);
                    });
                    satList.push(satellite.satelliteName);
                });

                for (let i = 0; i < towerCount; i++) {
                    let datasetsData = [];
                    satList.forEach(currentSatName => {
                        const signal = data
                            .find(satellite => satellite.satelliteName === currentSatName).signals
                            .find(signal => signal.towerId === i + 1);

                        datasetsData.push(signal ? signal.signalQuality : null);
                    });

                    datasets.push({
                        data: datasetsData,
                    });
                }

                for (let i = 0; i < towerCount; i++) {
                    let img = new Image(24, 24);
                    img.src = '/images/chart/base.png';
                    let pointStyle = img;

                    if (i < 16) {
                        img = new Image(24, 24);
                        img.src = `/images/chart/${i + 1}.png`;
                        pointStyle = img;
                    }

                    datasets[i] = {
                        ...datasets[i],
                        type: 'scatter',
                        radius: 10,
                        hoverRadius: 10,
                        label: i + 1,
                        backgroundColor: 'white',
                        pointStyle: pointStyle
                    };
                }

                data.forEach(satellite => {
                    satellite.signals.forEach(signal => {
                        listBoxOptions.push({
                            label: `${satellite.satelliteName}#${signal.towerId}`,
                            satelliteName: satellite.satelliteName,
                            signalQuality: signal.signalQuality,
                            towerId: signal.towerId
                        });
                    });
                });

                setSatelliteList(satList);
                setSignalDatasets(datasets);
                setListBoxOptions(listBoxOptions);
                chartRef.current.refresh();
            })
            .catch(error => {
                console.error(error);
            });
    };

    const sensorTrackEntryTemplate = (satelliteData) => {
        return (
            <div className="grid">
                <div className="col-7 flex align-items-center">
                    <span className="vertical-align-middle font-bold text-xl">{satelliteData.satelliteName}</span>
                </div>
                <div className="col-5 flex align-items-center justify-content-end">
                    <img
                        src={satelliteData.towerId < 16 ? `/images/chart/${satelliteData.towerId}.png` : '/images/chart/base.png'}
                        width={32}
                    />
                </div>
            </div>
        );
    };

    return (
        <div className="grid p-fluid">
            <div className="col-12 md:col-10">
                <Chart
                    style={{height: '600px'}}
                    type="line"
                    data={{
                        labels: satelliteList,
                        datasets: [...signalDatasets, highlightDataset],
                    }}
                    options={chartOptions}
                    ref={chartRef}
                />
                <div className="grid">
                    <div className="col-12 md:col-4">
                        <div className="border-1 border-50 border-round flex justify-content-between p-3">
                            <div className="w-full">
                                <span className="block text-500 font-medium mb-3 text-2xl">Satellite</span>
                                <div className="p-inputgroup mt-3">
                                    <span className="p-inputgroup-addon w-10rem">Satellite Name</span>
                                    <InputText
                                        value={selectedSignalDetails && selectedSignalDetails.satelliteName}
                                        disabled={true}
                                    />
                                </div>
                                <div className="p-inputgroup mt-3">
                                    <span className="p-inputgroup-addon w-10rem">Dimensions</span>
                                    <InputText
                                        value={selectedSignalDetails && selectedSignalDetails.dimensions}
                                        disabled={true}
                                    />
                                    <span className="p-inputgroup-addon">cm</span>
                                </div>
                                <div className="p-inputgroup mt-3">
                                    <span className="p-inputgroup-addon w-10rem">Perigee Altitude</span>
                                    <InputText
                                        value={selectedSignalDetails && selectedSignalDetails.perigeeAltitude}
                                        disabled={true}
                                    />
                                    <span className="p-inputgroup-addon">km</span>
                                </div>
                                <div className="p-inputgroup mt-3">
                                    <span className="p-inputgroup-addon w-10rem">Apogee Altitude</span>
                                    <InputText
                                        value={selectedSignalDetails && selectedSignalDetails.apogeeAltitude}
                                        disabled={true}
                                    />
                                    <span className="p-inputgroup-addon">km</span>
                                </div>
                            </div>
                            <div className="flex align-items-center justify-content-center bg-primary border-round"
                                 style={{width: '2.5rem', height: '2.5rem'}}>
                                <i className="fa-solid fa-satellite primary-text text-2xl"></i>
                            </div>
                        </div>
                    </div>
                    <div className="col-12 md:col-4">
                        <div className="border-1 border-50 border-round flex justify-content-between p-3">
                            <div className="w-full">
                                <span className="block text-500 font-medium mb-3 text-2xl">Tower</span>
                                <div className="p-inputgroup mt-3">
                                    <span className="p-inputgroup-addon w-10rem">Tower Number</span>
                                    <InputText
                                        value={selectedSignalDetails && selectedSignalDetails.towerId}
                                        disabled={true}
                                    />
                                </div>
                                <div className="p-inputgroup mt-3">
                                    <span className="p-inputgroup-addon w-10rem">City</span>
                                    <InputText
                                        value={selectedSignalDetails && selectedSignalDetails.city}
                                        disabled={true}
                                    />
                                </div>
                                <div className="p-inputgroup mt-3">
                                    <span className="p-inputgroup-addon w-10rem">Latitude</span>
                                    <InputText
                                        value={selectedSignalDetails && selectedSignalDetails.latitude}
                                        disabled={true}
                                    />
                                    <span
                                        className="p-inputgroup-addon">{selectedSignalDetails ? selectedSignalDetails.latitudePole : '° N'}</span>
                                </div>
                                <div className="p-inputgroup mt-3">
                                    <span className="p-inputgroup-addon w-10rem">Longitude</span>
                                    <InputText
                                        value={selectedSignalDetails && selectedSignalDetails.longitude}
                                        disabled={true}
                                    />
                                    <span
                                        className="p-inputgroup-addon">{selectedSignalDetails ? selectedSignalDetails.longitudeHemisphere : '° E'}</span>
                                </div>
                            </div>
                            <div className="flex align-items-center justify-content-center bg-primary border-round"
                                 style={{width: '2.5rem', height: '2.5rem'}}>
                                <i className="fa-solid fa-tower-cell primary-text text-2xl"></i>
                            </div>
                        </div>
                    </div>
                    <div className="col-12 md:col-4">
                        <div className="border-1 border-50 border-round flex justify-content-between p-3">
                            <div className="w-full">
                                <span className="block text-500 font-medium mb-3 text-2xl">Signal Quality</span>
                                <div className="w-full text-center ">
                                    <Knob
                                        value={selectedSignalDetails && selectedSignalDetails.signalQuality || 0}
                                        min={0} max={100} strokeWidth={10} size={200} readOnly
                                        valueColor={selectedSignalDetails &&
                                            (selectedSignalDetails.signalQuality < 25 ? 'var(--red-300)' :
                                                    selectedSignalDetails.signalQuality < 75 ?
                                                        'var(--yellow-300)' : 'var(--green-300)'
                                            ) || 'var(--green-300)'}
                                        valueTemplate={'{value}%'}
                                    />
                                </div>
                            </div>
                            <div className="flex align-items-center justify-content-center bg-primary border-round"
                                 style={{width: '2.5rem', height: '2.5rem'}}>
                                <i className="fa-solid fa-signal primary-text text-2xl"></i>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
            <div className="col-12 md:col-2 mt-4">
                <ListBox filter value={selectedSignal} onChange={(e) => {
                    e.value && setSelectedSignal({
                        satelliteName: e.value.satelliteName,
                        towerId: e.value.towerId,
                        signalQuality: e.value.signalQuality,
                        label: e.value.label
                    });
                }} options={listBoxOptions}
                         itemTemplate={(option) => sensorTrackEntryTemplate(option)} optionLabel={"label"}
                         className="w-full"/>
            </div>
        </div>
    );
};

export default SignalChart;

WeatherChart:
import {Chart} from "primereact/chart";
import {Button} from "primereact/button";
import React, {useEffect, useRef, useState} from "react";
import {Dialog} from "primereact/dialog";
import {InputTextarea} from "primereact/inputtextarea";
import {Toast} from "primereact/toast";
import {ShowcaseService} from "@/service/ShowcaseService";

const WeatherChart = () => {
    const toast = useRef();
    const chartRef = useRef();
    const [chartLabels, setChartLabels] = useState([]);
    const [chartDataRaw, setChartDataRaw] = useState({});
    const [chartNotes, setChartNotes] = useState([]);
    const [chartData, setChartData] = useState({});
    const [noteDialogVisible, setNoteDialogVisible] = useState(false);
    const [selectedNote, setSelectedNote] = useState("");
    const [selectedNoteDetails, setSelectedNoteDetails] = useState(undefined);
    const dataKeys = [
        "highestRecord",
        "highestAverage",
        "lowestAverage",
        "lowestRecord"
    ];
    const chartOptions = {
        maintainAspectRatio: false,
        pointHitRadius: 5,
        pointHoverRadius: 5,
        animation: false,
        scales: {
            x: {},
            y: {
                max: 50,
                min: -30
            }
        },
        onClick: (event, element) => {
            if (event.type === "click" && element.length) {
                setSelectedNoteDetails({
                    datasetIndex: element[0].datasetIndex,
                    monthIndex: element[0].index
                });

                if (element.length > 1) {
                    setSelectedNote(
                        chartNotes[element[0].index][dataKeys[element[0].datasetIndex]]
                    );
                } else {
                    setSelectedNote("");
                }

                setNoteDialogVisible(true);
            }
        }
    };

    useEffect(() => {
        let labels = [];
        let temperatureData = {};
        let noteData = [];

        dataKeys.forEach(key => {
            temperatureData[key] = [];
        });

        ShowcaseService.fetchWeatherData()
            .then(weatherData => {
                weatherData.forEach(monthWeather => {
                    labels.push(monthWeather.month);
                    dataKeys.forEach(key => {
                        temperatureData[key].push(monthWeather[key]);
                    });
                    noteData.push(monthWeather.notes);
                });

                setChartNotes(noteData);
                setChartLabels(labels);
                setChartDataRaw({
                    chartLabels: labels,
                    temperatureData: temperatureData,
                });
            })
            .catch(error => {
                console.error(error);
            });
    }, []);

    useEffect(() => {
        let noteLineData = [];
        chartNotes.forEach((monthNote, index) => {
            dataKeys.forEach(key => {
                if (monthNote[key] && monthNote[key].trim() !== '') {
                    noteLineData.push({
                        x: chartLabels[index],
                        y: chartDataRaw.temperatureData[key][index],
                        note: monthNote[key]
                    });
                }
            });
        });

        const img = new Image(75, 75);
        img.src = '/images/chart/note.png';

        const noteDataset = {
            label: 'Notes',
            data: noteLineData,
            type: 'scatter',
            borderColor: '#8dd0ff',
            backgroundColor: '#8dd0ff',
            tension: 0.4,
            pointStyle: img
        };

        chartDataRaw.temperatureData &&
        setChartData({
            labels: chartDataRaw.chartLabels,
            datasets: [
                {
                    label: 'Highest Record',
                    data: chartDataRaw.temperatureData[dataKeys[0]],
                    borderColor: '#bb2d3b',
                    backgroundColor: '#bb2d3b',
                    tension: 0.4
                },
                {
                    label: 'Highest Average',
                    data: chartDataRaw.temperatureData[dataKeys[1]],
                    borderColor: '#d76b11',
                    backgroundColor: '#d76b11',
                    tension: 0.4
                },
                {
                    label: 'Lowest Average',
                    data: chartDataRaw.temperatureData[dataKeys[2]],
                    borderColor: '#1bab80',
                    backgroundColor: '#1bab80',
                    tension: 0.4
                },
                {
                    label: 'Lowest Record',
                    data: chartDataRaw.temperatureData[dataKeys[3]],
                    borderColor: '#5e38a4',
                    backgroundColor: '#5e38a4',
                    tension: 0.4
                },
                noteDataset
            ]
        });
        chartRef.current.refresh();
    }, [chartNotes]);

    const saveNote = (noteRemoval) => {
        setChartNotes(prevNoteList => {
            let newNoteList = [...prevNoteList];
            newNoteList[selectedNoteDetails.monthIndex][
                dataKeys[selectedNoteDetails.datasetIndex]
                ] = noteRemoval ? '' : selectedNote;

            return newNoteList;
        });

        toast.current.show({
            severity: 'success',
            summary: 'Success',
            detail: noteRemoval || selectedNote === '' ? 'Message Removed' : 'Message Saved',
            life: 3000
        });
    };

    const removalButtonVisible = selectedNote.trim() !== "";
    const noteDialogTitle =
        selectedNoteDetails &&
        `${removalButtonVisible ? 'Edit Note' : 'Add Note'} for 
                    ${
            chartData.datasets[selectedNoteDetails.datasetIndex].label
        } on 
                    ${chartLabels[selectedNoteDetails.monthIndex]}`;

    const noteDialogFooter = (
        <>
            <Button
                visible={removalButtonVisible}
                type="button"
                label="Remove"
                icon="pi pi-trash"
                onClick={() => {
                    saveNote(true);
                    setNoteDialogVisible(false);
                }}
                text
            />
            <Button
                type="button"
                label="Cancel"
                icon="pi pi-times"
                onClick={() => setNoteDialogVisible(false)}
                text
            />
            <Button
                type="button"
                label="Save"
                icon="pi pi-check"
                onClick={() => {
                    saveNote(false);
                    setNoteDialogVisible(false);
                }}
                text
            />
        </>
    );

    return (
        <div className="grid p-fluid">
            <Toast ref={toast}/>
            <div className="col-12">
                <Chart
                    type="line"
                    style={{height: "600px"}}
                    data={chartData}
                    options={chartOptions}
                    ref={chartRef}
                />
            </div>
            <Dialog
                header={noteDialogTitle}
                visible={noteDialogVisible}
                position="top"
                blockScroll
                onHide={() => setNoteDialogVisible(false)}
                draggable={false}
                resizable={false}
                dismissableMask={true}
                closeOnEscape={true}
                footer={noteDialogFooter}
                closable={false}
            >
                <div className="grid p-fluid">
                    <div className="col-12">
                        <InputTextarea
                            rows={5}
                            cols={30}
                            value={selectedNote}
                            autoResize
                            onChange={(e) => setSelectedNote(e.target.value)}
                        />
                    </div>
                </div>
            </Dialog>
        </div>
    );
};

export default WeatherChart;

DevelopmentPlanning:
import {Button} from "primereact/button";
import React, {useEffect, useState} from "react";
import {Dropdown} from "primereact/dropdown";
import {MultiSelect} from "primereact/multiselect";
import {DataTable} from "primereact/datatable";
import {Column} from "primereact/column";
import {InputText} from "primereact/inputtext";
import {Checkbox} from "primereact/checkbox";
import {Chip} from "primereact/chip";
import {Card} from "primereact/card";
import {ShowcaseService} from "@/service/ShowcaseService";

const DevelopmentPlanning = () => {
    const [selectedTickets, setSelectedTickets] = useState([]);
    const [selectedTicketsMap, setSelectedTicketsMap] = useState({});
    const [tickets, setTickets] = useState([]);

    const [team, setTeam] = useState({});
    const [teams, setTeams] = useState([]);
    const [plan, setPlan] = useState([]);

    useEffect(() => {
        ShowcaseService.fetchDevTeamData()
            .then(teamsData => {
                teamsData.forEach(team => {
                    team.label = team.name;
                });

                setTeams(teamsData);
            })
            .catch(error => {
                console.error(error);
            });

        ShowcaseService.fetchDevTeamPlanSample()
            .then(samplePlan => {
                setPlan(samplePlan);
            })
            .catch(error => {
                console.error(error);
            });
    }, []);

    const addTicket = (ticket) => {
        setSelectedTickets(prevSelectedTickets => {
            let newSelectedTickets = [...prevSelectedTickets];

            newSelectedTickets.push({
                id: ticket.id,
                team: team,
                ticketName: ticket.name,
                ticketLabel: `${ticket.label} (${team.name})`,
                estimatedTime: ticket.estimatedTime,
                join: false
            });

            return newSelectedTickets;
        });
    };

    const removeTicket = (ticket) => {
        setSelectedTickets([...selectedTickets].filter(selectedTicket => (selectedTicket.id !== ticket.id)));
    };

    const joinBodyTemplate = (rowData, row) => {
        if (row.rowIndex === 0) {
            return <div></div>;
        }
        return <div style={{textAlign: 'center'}}>
            <Checkbox tooltip={"Start at the same time with the ticket above"} tooltipOptions={{showDelay: 300}}
                      onChange={e => {
                          setSelectedTickets(prevSelectedTickets => {
                              let newSelectedTickets = [...prevSelectedTickets];
                              newSelectedTickets[row.rowIndex].join = e.checked;
                              return newSelectedTickets;
                          });
                      }} checked={selectedTickets[row.rowIndex].join === true}/>
        </div>;
    };

    const estimatedTimeBodyTemplate = (rowData, row) => {
        return (
            <div className="p-inputgroup">
                <InputText size={3} className="inputfield w-full" keyfilter={"pint"}
                           value={selectedTickets[row.rowIndex].estimatedTime}
                           onChange={(e) => {
                               setSelectedTickets(prevSelectedTickets => {
                                   let newSelectedTickets = [...prevSelectedTickets];
                                   newSelectedTickets[row.rowIndex].estimatedTime = Number.parseInt(e.target.value);
                                   return newSelectedTickets;
                               });
                           }} placeholder={"time"}
                />
                <span className="p-inputgroup-addon">{"days"}</span>
            </div>
        );
    };

    const deleteTicketBodyTemplate = (rowData, row) => {
        return (
            <Button className="p-button-danger p-button-icon-only" icon="pi pi-trash" onClick={() => {
                const deletedTicket = selectedTickets[row.rowIndex];
                setSelectedTickets(prevSelectedTickets => {
                    let newSelectedTickets = [...prevSelectedTickets];
                    newSelectedTickets.splice(row.rowIndex, 1);
                    return newSelectedTickets;
                });
                setSelectedTicketsMap(prevSelectedTicketsMap => {
                    let newSelectedTicketsMap = {...prevSelectedTicketsMap};
                    newSelectedTicketsMap[deletedTicket.team.id] = selectedTicketsMap[deletedTicket.team.id]
                        .filter(item => item.id !== deletedTicket.id);
                    return newSelectedTicketsMap;
                });
            }}/>
        );
    };

    const deleteBatchBodyTemplate = (rowData, row) => {
        return (
            <Button className="p-button-danger p-button-icon-only mr-3" icon="pi pi-trash" onClick={() => {
                setPlan(prevPlan => {
                    let newPlan = [...prevPlan];
                    newPlan.splice(row.rowIndex, 1);
                    return newPlan;
                });
            }}/>
        );
    };

    const addTicketBatch = () => {
        if (selectedTickets.length === 0) {
            return;
        }

        let ticketBatch = [];
        let ticketGroup = [selectedTickets[0].ticketLabel];
        let totalEstimatedTime = 0;
        let currentGroupTime = selectedTickets[0].estimatedTime;

        selectedTickets.forEach((ticket, index) => {
            if (index === 0) {
                return;
            }

            if (ticket.join) {
                ticketGroup.push(ticket.ticketLabel);
                currentGroupTime = Math.max(currentGroupTime, ticket.estimatedTime);
            } else {
                ticketBatch.push(ticketGroup);
                totalEstimatedTime += currentGroupTime;
                ticketGroup = [ticket.ticketLabel];
                currentGroupTime = ticket.estimatedTime;
            }
        });

        ticketBatch.push(ticketGroup);
        totalEstimatedTime += currentGroupTime;

        setPlan(prevPlan => {
            let newPlan = [...prevPlan];
            newPlan.push({
                ticketBatch: ticketBatch, estimatedTime: totalEstimatedTime
            });

            return newPlan;
        });
    };

    const clearTicketBatch = () => {
        setSelectedTickets([]);
        setSelectedTicketsMap([]);
        setTeam({});
    };

    const ticketBatchBodyTemplate = (rowData) => {
        return (
            <div>
                {rowData.ticketBatch.map(ticketGroup => {
                    return (
                        <div className="card p-0 m-1 border-primary-800">
                            {ticketGroup.map(ticket => <Chip className="m-1 bg-primary font-bold" label={ticket}/>)}
                        </div>
                    );
                })}
            </div>
        );
    };

    return (
        <div className="grid">
            <div className="col-12 md:col-6">
                <div className="grid p-fluid">
                    <div className="col-12">
                        <Card title="Tickets"
                              subTitle="Select Teams and Tickets to create Ticket Batch and add it to the Plan">
                            <div className="grid justify-content-end">
                                <div className="col-12 md:col-6">
                                    <Dropdown value={team} options={teams} placeholder="Select Team"
                                              className="inputfield w-full"
                                              tooltip="Add more tickets by selecting other teams"
                                              onChange={e => {
                                                  const team = e.target.value;
                                                  setTeam(team);

                                                  const ticketsData = [...team.tickets];

                                                  ticketsData.forEach(ticket => {
                                                      ticket.label = ticket.name;
                                                  });

                                                  setTickets(ticketsData);
                                              }}/>
                                </div>
                                <div className="col-12 md:col-6">
                                    <MultiSelect filter showSelectAll={false} value={selectedTicketsMap[team.id]}
                                                 options={tickets} disabled={!team.name}
                                                 placeholder="Select Ticket(s)" maxSelectedLabels={3}
                                                 onChange={(e) => {
                                                     let newSelectedTicketsMap = {...selectedTicketsMap};

                                                     if (newSelectedTicketsMap[team.id]) {
                                                         newSelectedTicketsMap[team.id]
                                                             .find(selectedTicket => selectedTicket.id === e.selectedOption.id) ? removeTicket(e.selectedOption) : addTicket(e.selectedOption);
                                                     } else {
                                                         addTicket(e.selectedOption);
                                                     }

                                                     newSelectedTicketsMap[team.id] = e.target.value;
                                                     setSelectedTicketsMap(newSelectedTicketsMap);
                                                 }}/>
                                </div>
                                <div className="col-12" hidden={selectedTickets.length < 1}>
                                    <DataTable value={selectedTickets} size="small"
                                               onRowReorder={(e) => setSelectedTickets(e.value)}>
                                        <Column field="join" className="text-center" header="Combine Above"
                                                style={{width: '10rem'}} body={joinBodyTemplate}></Column>
                                        <Column field="ticketLabel" header="Ticket"></Column>
                                        <Column field="estimatedTime" header="Estimated Time"
                                                style={{width: '10rem'}} body={estimatedTimeBodyTemplate}></Column>
                                        <Column style={{width: '3rem'}} body={deleteTicketBodyTemplate}></Column>
                                    </DataTable>
                                </div>
                            </div>
                        </Card>
                    </div>
                    <div className="col-12 md:col-6">
                        <Button label="Clear" className="p-button-danger font-bold" icon="pi pi-trash"
                                iconPos="right" onClick={clearTicketBatch}/>
                    </div>
                    <div className="col-12 md:col-6">
                        <Button label="Add" className="p-button-success font-bold" icon="pi pi-chevron-right"
                                iconPos="right" onClick={addTicketBatch}/>
                    </div>
                </div>

            </div>
            <div className="col-12 md:col-6">
                <Card title="Plan" subTitle="Observe the Ticket Batches and remove if necessary">
                    <DataTable value={plan}>
                        <Column field="ticketBatch" header="Ticket Batch" body={ticketBatchBodyTemplate}></Column>
                        <Column field="estimatedTime" header="Minimum Estimated Time"
                                style={{width: '10rem', textAlign: 'center', fontWeight: 'bolder'}}></Column>
                        <Column style={{width: '3em'}} body={deleteBatchBodyTemplate}></Column>
                    </DataTable>
                </Card>
            </div>
        </div>
    );
};

export default DevelopmentPlanning;

GamePlanning:
import {Button} from "primereact/button";
import React, {useEffect, useState} from "react";
import {DataTable} from "primereact/datatable";
import {Column} from "primereact/column";
import {Card} from "primereact/card";
import {Dialog} from "primereact/dialog";
import {TeamDetail} from "@/components/TeamDetail";
import {ShowcaseService} from "@/service/ShowcaseService";

const GamePlanning = () => {
    const [previousTeamsDialog, setPreviousTeamsDialog] = useState(false);
    const [teams, setTeams] = useState([{}]);
    const [playingTeams, setPlayingTeams] = useState([]);
    const [previousTeams, setPreviousTeams] = useState([]);

    useEffect(() => {
        ShowcaseService.fetchGameSample()
            .then(gameSample => {
                setPreviousTeams(gameSample);
                setPlayingTeams(gameSample);
            })
            .catch(error => {
                console.error(error);
            });
    }, []);

    const onTeamDetailsUpdate = (index, field, value) => {
        setTeams(prevTeams => {
            let newTeams = [...prevTeams];
            newTeams[index][field] = value;

            return newTeams;
        });
    };

    const onTeamDelete = (index) => {
        setTeams(prevTeams => {
            let newTeams = [...prevTeams];
            newTeams.splice(index, 1);
            return newTeams;
        });
    };

    return (
        <React.Fragment>
            <Dialog
                header={'Previous teams'}
                visible={previousTeamsDialog}
                resizable={false}
                draggable={false}
                dismissableMask={true}
                onHide={() => setPreviousTeamsDialog(false)}
            >
                <div className="grid p-fluid">
                    <div className="col-12">
                        <DataTable value={previousTeams} paginator={true} rows={5}>
                            <Column field="teamName" header={"Team Name"}></Column>
                            <Column field="teamSize" header={"Team Size"}></Column>
                            <Column
                                field="teamColor"
                                header="Team Color"
                                body={team => (
                                    <div
                                        style={{
                                            background: team.teamColor,
                                            height: '30px',
                                            borderRadius: '3px',
                                        }}
                                    >
                                        &nbsp;
                                    </div>
                                )}
                            ></Column>
                            <Column
                                body={item => (
                                    <Button
                                        className="p-button-success p-button-icon-only"
                                        icon="pi pi-plus"
                                        onClick={() => {
                                            setTeams(prevTeams => {
                                                let newTeams = [...prevTeams];
                                                item.isCustomTeamColor = (item.teamColor.charAt(0) === '#');
                                                if (Object.keys(newTeams[newTeams.length - 1]).length === 0) {
                                                    newTeams[newTeams.length - 1] = item;
                                                } else {
                                                    newTeams.push(item);
                                                }

                                                return newTeams;
                                            });
                                            setPreviousTeamsDialog(false);
                                        }}
                                    />
                                )}
                            />
                        </DataTable>
                    </div>
                </div>
            </Dialog>
            <div className="grid">
                <div className="col-12 md:col-6">
                    <div className="grid p-fluid">
                        <div className="col-12">
                            <Card
                                title="Team Setup"
                                subTitle="Add your own new teams or select from previous teams to play the game"
                            >
                                <div className="grid p-fluid justify-content-end">
                                    <div className="col-12">
                                        {teams.map((item, index) => (
                                            <TeamDetail
                                                key={index}
                                                item={item}
                                                deletable={teams.length > 1}
                                                onDelete={() => onTeamDelete(index)}
                                                onUpdate={(field, value) => onTeamDetailsUpdate(index, field, value)}
                                            />
                                        ))}
                                    </div>
                                    <div className="col-12 md:col-6">
                                        <Button
                                            label={"Add New Team"}
                                            className="p-button"
                                            icon="pi pi-plus"
                                            iconPos="right"
                                            onClick={() => {
                                                setTeams(prevTeams => ([
                                                    ...prevTeams,
                                                    {},
                                                ]));
                                            }}
                                        />
                                    </div>
                                    <div className="col-12 md:col-6">
                                        <Button
                                            label={"Select From Previous Teams"}
                                            className="p-button"
                                            icon="pi pi-save"
                                            iconPos="right"
                                            onClick={() => {
                                                setPreviousTeamsDialog(true);
                                            }}
                                        />
                                    </div>
                                </div>
                            </Card>
                        </div>
                        <div className="col-12 md:col-6">
                            <Button
                                label={'Clear'}
                                className="p-button-danger font-bold"
                                icon="pi pi-trash"
                                iconPos="right"
                                onClick={() => setTeams([{}])}
                            />
                        </div>
                        <div className="col-12 md:col-6">
                            <Button
                                label={'Play'}
                                className="p-button-success font-bold"
                                icon="pi pi-play"
                                iconPos="right"
                                onClick={() => {
                                    let teamsToPlay = [];
                                    const currTeams = [...teams];

                                    currTeams.forEach(team => {
                                        if (
                                            team.teamName &&
                                            team.teamSize &&
                                            team.teamColor
                                        ) {
                                            const redundantTeam = teamsToPlay.find(
                                                teamToPlay => (
                                                    team.teamName ===
                                                    teamToPlay.teamName &&
                                                    team.teamSize ===
                                                    teamToPlay.teamSize &&
                                                    team.teamColor ===
                                                    teamToPlay.teamColor
                                                )
                                            );

                                            if (!redundantTeam) {
                                                teamsToPlay.push(team);
                                            }
                                        }
                                    });

                                    if (teamsToPlay.length > 0) {
                                        setTeams([{}]);
                                        setPlayingTeams(teamsToPlay);
                                        setPreviousTeams(prevPreviousTeams => {
                                            let newPreviousTeams = [];

                                            teamsToPlay.forEach(teamToPlay => {
                                                const alreadyAdded = prevPreviousTeams.find(
                                                    previousTeam => (
                                                        previousTeam.teamName ===
                                                        teamToPlay.teamName &&
                                                        previousTeam.teamSize ===
                                                        teamToPlay.teamSize &&
                                                        previousTeam.teamColor ===
                                                        teamToPlay.teamColor
                                                    )
                                                );

                                                if (!alreadyAdded) {
                                                    newPreviousTeams.push(teamToPlay);
                                                }
                                            });

                                            return [
                                                ...prevPreviousTeams,
                                                ...newPreviousTeams,
                                            ];
                                        });
                                    }
                                }}
                            />
                        </div>
                    </div>
                </div>
                <div className="col-12 md:col-6">
                    <Card
                        title="Game"
                        subTitle="Observe teams playing in the current game"
                    >
                        <div className="grid">
                            {playingTeams.map(playingTeam => {
                                const teamSize = parseInt(
                                    playingTeam.teamSize
                                );

                                return (
                                    <div className="col-12 md:col-6">
                                        <div
                                            className="card"
                                            style={{
                                                borderColor:
                                                playingTeam.teamColor,
                                                borderWidth: "medium",
                                            }}
                                        >
                                            <h4>{playingTeam.teamName}</h4>
                                            <p>{`${teamSize} ${
                                                teamSize > 1
                                                    ? 'people'
                                                    : 'person'
                                            }`}</p>
                                        </div>
                                    </div>
                                );
                            })}
                        </div>
                    </Card>
                </div>
            </div>
        </React.Fragment>
    );
};

export default GamePlanning;

TeamDetail:
import React, {useEffect, useState} from 'react';
import {Dropdown} from 'primereact/dropdown';
import {InputText} from "primereact/inputtext";
import {Button} from "primereact/button";
import {ColorPicker} from "primereact/colorpicker";

export const TeamDetail = ({item, deletable, onUpdate, onDelete}) => {
    const [predefinedColors, setPredefinedColors] = useState([]);
    const defaultColorPickerColor = '#909090';

    useEffect(() => {
        const presetColors = [
            "var(--red-500)",
            "var(--orange-500)",
            "var(--yellow-500)",
            "var(--green-500)",
            "var(--cyan-500)",
            "var(--blue-500)",
            "var(--purple-500)",
            "var(--pink-500)"
        ];

        let colorOptions = [{
            type: 'custom',
        }];
        presetColors.forEach(color => {
            colorOptions.push({
                type: 'preset',
                content: color
            });
        });

        setPredefinedColors(colorOptions);
    }, []);

    return (
        <div className="grid">
            <div className="col-12 md:col-4">
                <InputText className="inputfield w-full"
                           value={item.teamName ? item.teamName : ""}
                           onChange={(e) =>
                               onUpdate("teamName", e.target.value)} placeholder={"Team Name"}
                />
            </div>
            <div className="col-12 md:col-3">
                <div className="p-inputgroup">
                    <InputText className="inputfield w-full" keyfilter="pint"
                               value={item.teamSize ? item.teamSize : ""}
                               onChange={(e) =>
                                   onUpdate("teamSize", e.target.value)} placeholder={"Team Size"}
                    />
                    <span className="p-inputgroup-addon">ppl</span>
                </div>
            </div>
            <div
                className={`col-${item.isCustomTeamColor ? 11 : 12} md:col-${item.isCustomTeamColor ? 3 : 4}`}>
                <Dropdown
                    dropdownIcon={item.isCustomTeamColor ? "pi pi-arrow-right" : ""}
                    className="inputfield w-full"
                    options={predefinedColors}
                    placeholder={"Team Color"}
                    onChange={(e) => {
                        if (e.value.type === 'custom') {
                            onUpdate("isCustomTeamColor", true)
                            onUpdate("teamColor", defaultColorPickerColor)
                        } else {
                            onUpdate("isCustomTeamColor", false)
                            onUpdate("teamColor", e.value.content)
                        }
                    }}
                    itemTemplate={(rowData) => {
                        if (rowData.type === 'custom') {
                            return (<div><p>{"Custom Color"}</p></div>)
                        }
                        return <div style={{
                            background: rowData.content,
                            height: '30px',
                            marginTop: '-5px',
                            marginBottom: '-5px',
                            borderRadius: '3px'
                        }}>&nbsp;</div>
                    }}
                    valueTemplate={() => {
                        if (!item.isCustomTeamColor && !item.teamColor || item.isCustomTeamColor) {
                            return <div><p>{"Team Color"}</p></div>
                        }

                        return <div style={{
                            background: item.teamColor,
                            borderRadius: '3px',
                            color: 'black',
                            textAlign: "left"
                        }}><p className="ml-2">Team Color</p></div>
                    }}
                />
            </div>
            {
                item.isCustomTeamColor && (
                    <div className="col-1 md:col-1 flex justify-content-center">
                        <ColorPicker id="colorPicker" hidden={!item.isCustomTeamColor}
                                     value={item.teamColor.charAt(0) === '#' ? item.teamColor : defaultColorPickerColor}
                                     style={{marginTop: '.3rem'}} onChange={(e) => {
                            onUpdate("teamColor", `#${e.value}`)
                        }}></ColorPicker>
                    </div>
                )
            }
            <div className="col-12 md:col-1 flex justify-content-center">
                <Button className="p-button-danger p-button-icon-only" icon="pi pi-trash" onClick={() => {
                    onDelete()
                }} disabled={!deletable}/>
            </div>
        </div>
    );
}
